mapper.select:
SELECT * FROM ( SELECT ( @i := @i + 1 ) rk, t.*, (SELECT columnName FROM sys_column WHERE columnId = t.columnId ) AS columnName, (SELECT nickname FROM m_user WHERE id = t.createBy AND valid = '1') as nickName, (SELECT dict_label FROM sys_dict_data WHERE dict_type = 'sys_label_status' AND dict_value = t.valid ) AS isAvailable FROM sys_label t, ( SELECT @i := 0 ) rk where 1 = 1
and labelId = #{labelId}
) AS label where rk between (#{page}-1)*#{limit} + 1 and #{page}*#{limit} ORDER BY rk

select count(1) from sys_label
where 1 = 1

    and labelId = #{labelId}
